import React from 'react'
import axios, { post } from 'axios';

class FileUpload extends React.Component {

  constructor(props) {
    super(props);
    this.state ={
      file:null
    }
    this.onFormSubmit = this.onFormSubmit.bind(this)
    this.onChange = this.onChange.bind(this)
    this.fileUpload = this.fileUpload.bind(this)
    this.onCategoryChange = this.onCategoryChange.bind(this);
  }

  onFormSubmit(e){
    e.preventDefault() // Stop form submit
    this.fileUpload(this.state.file).then((response)=>{
      console.log(response.data);
    })
  }

  onChange(e) {
    this.setState({file:e.target.files[0]})
  }

  onCategoryChange(e){
        this.setState({ gender: e.target.value });
        console.log(gender)
  }

  fileUpload(file){
    const url = "http://10.0.0.129:5000/api/upload";
    //const url = 'http://example.com/file-upload';
    const formData = new FormData();
    formData.append('file',file)
    //console.log(formData)
    const config = {
        headers: {
            'content-type': 'multipart/form-data'
        }
    }
    return  post(url, formData,config)
  }

  render() {
    return (
      <form onSubmit={this.onFormSubmit}>
        <h1>File Upload</h1>
        <input type="file" onChange={this.onChange} />
        <button type="submit">Upload</button>

        <div>
              <select value={this.state.gender} onChange={this.onCategoryChange}> 
                  <option name="Grocery"> Grocery</option>
                  <option name="Shopping">Shopping</option>
                  <option name="Rent"> Rent</option>
                  <option name="House Maintainence"> Maintainence</option>
              </select>
        </div>


        
      </form>
   )
  }
}



